INSERT INTO `weibo_content_new` VALUES ('22', '乐观寸草心', '又快过年了。这段时间是小偷的活跃期，每年在春节前小毛贼都会采取行动，因为在这个时间多数家庭都准备了充足的资金，小偷抓住这个“机遇”捞外快是最佳选择，所以，希望朋友们忙于过年欢乐的同时不要疏忽大意。一旦抓住小偷可以打两下出出气不要造成严重伤害，最好劝他去偷贪官。记住，就这样做', '2015-01-01:2015-01-30', null, null, null, null);
