INSERT INTO `weibo_content_new` VALUES ('202', '广州日报', '【售票员阻止小偷上车被掌掴，没人帮忙心寒“以后再有小偷上车可能不会管了”】25日，40多岁的珠海公交售票员梁梅妹因阻止惯偷上车被狠狠掴了两巴掌，全车没一个人站出来，这让她很难过。梁梅妹当了14年售票员，曾多次提醒乘客注意小偷，这次却从没有一个乘客站出来。http://t.cn/RZmfcgp', '2015-01-01:2015-01-30', null, null, null, null);
