INSERT INTO `weibo_content_new` VALUES ('222', '陕西都市快报', '【看到有人拿这个，马上打110！小偷！】近日，吉林的张先生从银行取完钱放在车内，没想到，半路接孩子离开车辆不到3分钟，车内65万元现金就被偷走！警方发现，是小偷用汽车干扰器盗窃（下图这种仪器），能让周围车辆无法上锁！看到有人拿这个，立即报警扩散！别让小偷得逞！ via浙江公安', '2015-01-01:2015-01-30', null, null, null, null);
