INSERT INTO `weibo_content_new` VALUES ('412', '蓟县发布', '#冬季防扒提示#【掀帘盗窃】一些商场和商户为了保暖会在冬季挂上厚厚的门帘,掀起这些门帘比较费劲,女士甚至需要双手,这就给了小偷可乘之机,在您掀起门帘的瞬间,小偷就乘机盗窃。【提示】应该将重要财物贴身放置,最好放在身前的兜里,尽量不要放在大衣兜里,在掀帘时注意有无可疑人员尾随,准备盗窃。', '2015-01-01:2015-01-30', null, null, null, null);
