INSERT INTO `weibo_content_new` VALUES ('719', '马鞍山公安在线', '#在线播报#【别墅门上贴条已被偷光 小偷不信偷到1包5角硬币】郑先生因常年在外做生意，家中别墅总是空着，经常被小偷光顾。无奈之下，他贴了一张字条：“请小偷大哥善待我家的大门，我家已经被偷得很干净了，请早点离开。”可偏偏有一个小偷不信，在别墅搜到101个5角硬币后被抓。好把小偷哥你赢了', '2015-01-01:2015-01-30', null, null, null, null);
