INSERT INTO `weibo_content_new` VALUES ('2440', '孙毅警务室', '#民警微提示#【门缝塞广告 要及时清理】天气转暖，市民图方便出行将楼宇门敞开，恼人的小广告又卷土重来。很多小广告被塞在防盗门门缝里。民警提醒，这些小广告存有安全隐患，一定要及时清理。长期不清理的小广告会让小偷得到此户无人的信号。若长期不在家，最好让邻居帮忙及时清理门缝中的小广告。', '2015-04-01:2015-04-30', null, null, null, null);
