INSERT INTO `weibo_content_new` VALUES ('2521', '民警刘会永', '【两男子抢付茶叶蛋钱 奔驰未熄火被小偷开走】南京于先生下车和哥们抢付茶叶蛋钱，突然听到发动机一响，停在路边没熄火的奔驰车被一陌生男子开跑！最终警方将车追回，嫌疑人潘某落网。潘某说，原本只想偷电动车，没想到遇到个奔驰：任何时候，停车熄火拔钥匙、锁车门，哪怕你就站在车旁', '2015-04-01:2015-04-30', null, null, null, null);
