INSERT INTO `weibo_content_new` VALUES ('2528', 'CSU临五1407班', '这是今天...准确说应该是昨天开班会的时候拍摄的。班会主题是校园安全和文明寝室，由七班和八班共同完成哦～主页妞不仅了解了许多在寝室应该注意的事项，还学到了好几个防扒手的Tips呢班长大大还亲自给我们演示了小偷的行窃过程，更有希哥讲述亲身经历，也给在场的每一个人敲响了警钟！', '2015-04-01:2015-04-30', null, null, null, null);
