INSERT INTO `weibo_content_new` VALUES ('2531', '都市快报', '【两男子下车抢付茶叶蛋钱 奔驰没熄火被小偷开走】13日晚，南京一对老弟兄于先生和赵先生开车经过苜蓿园大街，停车在一家超市门口买吃的。赵先生拿了些茶叶蛋准备付钱，于先生赶紧冲下车，想抢在赵先生之前把钱给付了。这时，两人听到一阵发动机油门声，奔驰被人开走了。扬子晚报http://t.cn/RATQ8vs', '2015-04-01:2015-04-30', null, null, null, null);
