INSERT INTO `weibo_content_new` VALUES ('2630', '西安论坛', '#西安笨贼#【奇葩小偷偷三百来斤铁门，想卖破烂却被抓】穆师傅回家，走半道儿上，看见了正下坡的电动三轮车，发现车上的这扇门就是他家的！于是马上挡住车报了警，这才知道车上两人是看上了他家们准备偷走卖破烂。三百多斤的门七八人抬着都劲，不知道这两笨贼怎么弄到车上...http://t.cn/RAYAx5L', '2015-04-01:2015-04-30', null, null, null, null);
