INSERT INTO `weibo_content_new` VALUES ('2746', '广州海珠公安', '【手机放在这儿最容易被偷！】近期，宁波海曙警方根据破获的90余起手机盗窃案件分析，警方发现：公交车站候车、上车时最易被盗，用镊子作案占50%，手机放在上衣口袋占80%，小偷最喜欢苹果手机…看口袋里的防贼术↓↓最后提醒一句：防盗不光靠口袋，还要靠意识！', '2015-04-01:2015-04-30', null, null, null, null);
