INSERT INTO `weibo_content_new` VALUES ('2752', '麦子腐烂了', '管庄柏林爱乐小区的物业我就操了，已经被盗第二辆摩托车了，调监控看着小偷推出来从门口大大方方的推出大门，我就想问问物业的保安都是干嘛的，汽车被剐蹭挺小区里不管，所有的摄像头全部不是清晰版的，是为了推卸责任用的吗？我们交物业费干嘛？遇到事情就只会说你报警吧，他妈的是不是物业的责任啊？', '2015-04-01:2015-04-30', null, null, null, null);
