INSERT INTO `weibo_content_new` VALUES ('2755', 'Firefly因为润啊', '济南大学一教盗贼太猖獗，中午下去买饭的空，班里好多同学的书包都被翻了，并且有贵重物品丢失，在提高防范意识谴责小偷的同时，我们发现根本就投诉无门，校园警与保卫处都说无从寻找，因为没有摄像头，就算是有也不会开，我们遇到这种情况我们只能自认倒霉了吗？@济南校园新鲜事 @聚焦济南 @济南大学', '2015-04-01:2015-04-30', null, null, null, null);
