INSERT INTO `weibo_content_new` VALUES ('4209', '李晶1008', '【爆料】2015年6月11日，下午6点左右，广东湛江赤坎的沃尔玛超市内发生一起割喉事件。据了解是该女生为沃尔玛促销员发现小偷便举报，随后被小偷以菜刀割喉报复，女孩当场血流不止脸色惨白，热心人脱下衣服为其止血，并拨打了120，最终还是没有挽留住女孩的生命。愿一路走好！@湖北经视官方微博', '2015-06-01:2015-06-30', null, null, null, null);
