INSERT INTO `weibo_content_new` VALUES ('4396', '平安荆州', '【智障“小偷“作案，偷了扫帚偷雨衣】近日，石首某工业园区公司内发生两起盗窃案，石首警方通过巡逻，于26日凌晨3时，抓获正从该公司院墙外翻入的嫌疑人李某。经查，33岁的李某患有智障精神疾病，已先后两次翻入该企业，盗走包括扫帚、雨衣和旧解放鞋在内的数十元物资。李某已交其父母带回家中看管。', '2015-07-01:2015-07-30', null, null, null, null);
