INSERT INTO `weibo_content_new` VALUES ('4555', '瓷都晚报', '#景德镇爆料#【他两次制止了小偷，结果自己被抓了】7月4日晚，邹某经过新村西路时发现一小偷准备偷一辆高档摩托车，把小偷吓跑后他便去网吧上网，第二天凌晨5点从网吧出来后竟在老地方与小偷相遇，小偷怕行为暴露离去，这时邹某发现摩托车电源已接通，于是想把车占为己有，但在试车时被巡警抓获。', '2015-07-01:2015-07-30', null, null, null, null);
