INSERT INTO `weibo_content_new` VALUES ('4566', '斑马君爱武汉', '#武汉身边事#【宠物鹦鹉凌晨高声尖叫吓跑入室窃贼】 鹦鹉不仅会卖萌，小偷来了还会报警。昨日凌晨，一小偷撬开冶金街一居民家防盗网，翻窗入室盗窃，家中两只鹦鹉竟机敏地扑腾翅膀，发出尖叫，吓跑了窃贼，家里免遭损失。目前，冶金派出所正在追查那名入室窃贼。', '2015-07-01:2015-07-30', null, null, null, null);
