INSERT INTO `weibo_content_new` VALUES ('5921', '浔阳资讯', '#九江身边事#【机智汪星人当街咬住小偷 保住主人刚领回的工资】9月17日，于大爷去工商银行取工资，一名年轻男子从后面剪断了于大爷身上的背包。一直跟在于大爷身旁的小狗像是看懂了一样狂叫不止。听到小狗叫，于大爷赶紧回头发现包掉在地上，小狗死死地咬住年轻男子的裤腿。民警到达后，小狗才松开嘴。', '2015-09-01:2015-09-30', null, null, null, null);
