INSERT INTO `weibo_content_new` VALUES ('6107', '当代生活报', '【南宁#保安收钱放走小偷# 被起诉包庇罪】家住南宁市中心金朝阳1座的黎女士回家后发现小偷在盗窃自家东西，于是把小偷扭送给保安。谁知当班的保安钟某和陈某向小偷索取500元好处费后，将其放走。黎女士无奈只好报警。监控面前，钟某和陈某只好向兴宁警方承认事实。近日，他们被起诉犯有包庇罪。NNTV', '2015-09-01:2015-09-30', '116.5217', '39.958954', '朝阳区', '安全');
INSERT INTO `weibo_content_new` VALUES ('6108', '从菏而来', '#菏泽身边事#【菏泽：盗贼街头行窃被城管队员当场擒获】9月7日9时许，27岁的城管执法人员肖健巡逻至菏泽绿源小区门口附近时，突遇一名小偷将一中年妇女刚从银行取的一沓钞票盗走，并向南逃窜。肖健立即追了上去，追了大约五六十米，慢慢接近盗贼，成功将其擒获，使女士免受损失。(牡丹晚报)', '2015-09-01:2015-09-30', null, null, null, null);
