INSERT INTO `weibo_content_new` VALUES ('6176', '段郎说事', '男子抓小偷手被砍伤 5天后因欠医疗费无奈出院20岁小任，看到毛贼偷车果断出手，岂料被对方砍伤手臂，确诊为手肘开放性骨折，必须马上手术，但小偷已跑。低保家庭的父母只有向亲戚求助，借来2万元医治，但5天后，他还是因为欠费而无奈出院……小任说，事后车主默默离开，连谢谢都没说。（云南网）', '2015-09-01:2015-09-30', null, null, null, null);
