INSERT INTO `weibo_content_new` VALUES ('6180', '一支折翼的奥尔良烤翅', '骗子实在太可恶！前几天丢的手机（其实是被某出租车司机拿走的）每天一直在定位寻找手机位置，小偷也开不了机。今天收到条短信，还以为是手机有望找回，没想到是一条木马短信，苹果账号信息全被套走了。网站做的跟苹果网站一模一样，自认对信息风险防范意识也比较强，想来可能还是被捉到软肋，太不爽了', '2015-09-01:2015-09-30', null, null, null, null);
