INSERT INTO `weibo_content_new` VALUES ('6198', '丽丽猫儿儿', '全麻影响智商的这个论断绝对无稽，还是老话说的对，孩子这种生物生一回病长一次心眼，恢复期元宝的智商我们已经追不上了。清晨，小妞眼皮肿，我问：你的双眼皮去哪儿了？宝：被小偷偷走了...下午和她爹起腻，宝自言自语：爸爸你的胡子有点扎，我怒斥：你别老亲我闺女，妞：妈妈是我亲了他，艾玛，实诚', '2015-09-01:2015-09-30', null, null, null, null);
