INSERT INTO `weibo_content_new` VALUES ('6204', '孝感网槐荫论坛', '【小偷偷车出新招：安个滑轮就能骑走】昨日，微博网友 @宝贝回家孝感志愿者 揭露了偷车贼的最新作案工具——滑轮：电动车锁住前轮后小偷在前轮安装一个滑轮，锁就成摆设了。有电动车的朋友们注意了，一定要锁好后轮。别给小偷可乘之机！http://t.cn/Ryb183I', '2015-09-01:2015-09-30', null, null, null, null);
