INSERT INTO `weibo_content_new` VALUES ('6364', '小肚腩打败了杨柳腰', '力宝大厦物业不是做得好吗？请人天天早上在大厅弹钢琴，下个雨一排人帮我们收伞，简直然并卵！还不如帮我们看好办公室，全体去会议室开个会出来东西就被偷了，小偷在门口守了那么久保安没发现吗？看了监控简直一身汗话说突然好怀恋学校那无处不在的高清监控！@成都全接触 @成都晚报 @平安成都', '2015-09-01:2015-09-30', null, null, null, null);
