INSERT INTO `weibo_content_new` VALUES ('7826', '搞笑娘', '【打败你的不是天真，是无鞋……】某日晚，一股恶臭将正在熟睡的河南女子王某熏醒。她起来一看，家中遭贼了！因臭味很浓，王某判断小偷没走多久，于是喊邻居抓贼。果然，贼还没出楼道就被抓了。窃贼叫高某，平时很懒，几天不洗一次脚。此次行窃，他怕惊动屋主专门脱了鞋，没想到这脚臭把人给熏醒了', '2015-011-01:2015-011-30', null, null, null, null);
